        user: loadValue("dio_user", "{}"),

        notification: loadValue('notif', '0'),
        update: loadValue('update', '0'),
        notiff: loadValue('notiff', '0'),

        error: loadValue('error', '{}'),

        // WORLD
        townTypes: loadValue(WID + "_townTypes", "{}"),
